import React, { useEffect, useState, useRef } from "react";
// import ScrollToBottom from "react-scroll-to-bottom";
import './ChatContainercopy.css'

// import Chatinput from "./Chatinput";
export default function ChatContainercopy({ socket, username, room }) {
    const [currentMessage, setCurrentMessage] = useState("");
    const [messageList, setMessageList] = useState([]);
    const messageEndRef = useRef(null);

    useEffect(() => {
        scrollToBottom();
    }, [messageList]);

    const sendMessage = async () => {
        if (currentMessage !== "") {
            const room = room
            const author = username
            const content = currentMessage
            const time = new Date(Date.now()).getHours() + ":" + new Date(Date.now()).getMinutes()
            const date = new Date().slice(0, 10)
            const messageData = {
                room: room,
                author: username,
                content: currentMessage,
                time:
                    new Date(Date.now()).getHours() +
                    ":" +
                    new Date(Date.now()).getMinutes(),
                date: new Date().slice(0, 10)
            };

            await socket.emit("message-room", room, content, author, time, date);
            // setMessageList((list) => [...list, messageData]);
            setCurrentMessage("");
        }

    };


    function scrollToBottom() {
        messageEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }


    socket.off("room-messages").on("room-messagess", (roomMessages) => {
        // setMessageList((list) => [...list, data]);
        setCurrentMessage(roomMessages);
    });

    return (
        <>
            <div className='chat-container'>
                <div className="message-container">
                    {currentMessage.map((messageContent) => {
                        return (
                            <div
                                className="message" key={messageContent.time}
                                id={username === messageContent.author ? "you" : "other"}
                            >
                                <div>
                                    <div className="message-content">
                                        <p>{messageContent.message}</p>
                                    </div>
                                    <div className="message-meta">
                                        <p id="time">{messageContent.time}</p>
                                        <p id="author">{messageContent.author}</p>
                                    </div>
                                </div>
                            </div>
                        );
                    })}
                    <div ref={messageEndRef} />
                </div>
                <div className="chat-input" >
                    <input onChange={(e) => setCurrentMessage(e.target.value)} onKeyPress={(event) => {
                        event.key === "Enter" && sendMessage();
                    }}
                        value={currentMessage} type="text" className="txt-input" placeholder="Send a message..." />
                    <button onClick={sendMessage} type="submit" className="btn-submit" id="chat-submit">send</button>
                </div>
                {/* <Chatinput /> */}
            </div>
        </>
    )
}
