import React, { useState, useEffect, useRef } from "react";
import './ChatContainercopy.css'
import axios from "axios";
import { sendMessageRoute, recieveMessageRoute } from "../utils/APIRoutes";



import Chatinput from "./Chatinput";
const API_URL = process.env.REACT_APP_API_URL;


export default function ChatContainercopy({ currentChat, socket }) {

    const [messages, setMessages] = useState([]);
    const scrollRef = useRef();
    const [arrivalMessage, setArrivalMessage] = useState(null);

    const data = JSON.parse(localStorage.getItem(API_URL));

    useEffect(() => {
        async function fetchMessages() {
            const response = await axios.post(recieveMessageRoute, {
                from: data._id,
                to: currentChat._id,
            });
            setMessages(response.data);
        }
        fetchMessages();
    }, [currentChat, data._id]);

    useEffect(() => {
        if (socket.current) {
            socket.current.on("msg-recieve", (msg) => {
                setArrivalMessage({ fromSelf: false, message: msg });
            });
        }
    }, [socket]);

    useEffect(() => {
        if (arrivalMessage) {
            setMessages(prevMessages => [...prevMessages, arrivalMessage]);
        }
    }, [arrivalMessage]);

    useEffect(() => {
        if (scrollRef.current) {
            scrollRef.current.scrollIntoView({ behavior: "smooth" });
        }
    }, [messages]);

    const handleSendMsg = async (msg) => {
        socket.current.emit("send-msg", {
            to: currentChat._id,
            from: data._id,
            msg,
        });
        await axios.post(sendMessageRoute, {
            from: data._id,
            to: currentChat._id,
            message: msg,
        });
        setMessages(prevMessages => [...prevMessages, { fromSelf: true, message: msg }]);
    };
    return (
        <>
            <div className='chat-container'>
                <div className='chat-messages'>

                    <div className='message'>
                        <img
                            className="messageImg"
                            src='https://picsum.photos/200/300'
                            alt=""
                        />
                        <div className="messageText ">
                            <p>lorem sjf lkasdjlfkajsdflkasj</p>
                        </div>

                    </div>

                </div>
                <Chatinput onSendMsg={handleSendMsg} />
            </div>
        </>
    )
}
