import React, { useState, useEffect, useRef } from "react";
import { io } from "socket.io-client";
import { useAuthContext } from '../hooks/useAuthContext'

// CSS
import './Chat.css'

//Component
import ChatContainercopy from '../components/ChatContainercopy'
import Contacts from '../components/Contacts';

export default function Chat() {
    // const socket = io.connect("http://localhost:3001");
    const socket = useRef();
    const [currentRoom, setCurrentRoom] = useState([]);


    const [contacts, setContacts] = useState([]);
    const { user } = useAuthContext()
    const [currentUser, setCurrentUser] = useState(undefined);
    const [currentChat, setCurrentChat] = useState(undefined);
    // console.log(`http://localhost:3001/api/user/allusers/${user.id}`)

    useEffect(() => {
        const settingUser = async () => {

            if (!localStorage.getItem(process.env.REACT_APP_LOCALHOST_KE)) {
                console.log("login")
            } else {
                setCurrentUser(
                    await JSON.parse(
                        localStorage.getItem(process.env.REACT_APP_LOCALHOST_KEY)
                    )
                );
            }
        }
        settingUser()
    }, []);

    useEffect(() => {
        if (user) {
            socket.current = io("http://localhost:3001");
            socket.current.emit("new-user", currentUser._id);
        }
    }, [user]);
    console.log(currentUser._id)

    useEffect(() => {
        const fetchAllUser = async () => {
            if (user) {
                fetch(`http://localhost:3001/api/user/allusers/${user._id}`)
                    .then((res) => res.json())
                    .then((data) => {
                        setContacts(data)

                    });
            }
        }
        fetchAllUser()
    }, [user]);

    const handleChatChange = (chat) => {
        setCurrentChat(chat);
    };
    return (
        <>
            <div className='contain'>
                <Contacts socket={socket} contacts={contacts} changeChat={handleChatChange} />
                {/* <Contacts /> */}
                {/* <ChatContainercopy setMessage={messages} /> */}
                <ChatContainercopy userId={user._id} room={currentRoom} currentChat={currentChat} />

            </div>

        </>
    );
}
