import React, { useState, useEffect, useRef } from "react";
import { io } from "socket.io-client";
import { useAuthContext } from '../hooks/useAuthContext'

// CSS
import './Chat.css'

//Component
import ChatContainercopy from '../components/ChatContainercopy'
import Contacts from '../components/Contacts';

export default function Chat() {
    const socket = useRef();
    const [contacts, setContacts] = useState([]);
    const { user } = useAuthContext()
    const [currentChat, setCurrentChat] = useState(undefined);
    const [currentUser, setCurrentUser] = useState({});

    useEffect(() => {
        const settingCurrentUser = () => {
            if (!localStorage.getItem(process.env.REACT_APP_LOCALHOST_KEY)) {

            } else {
                setCurrentUser(
                    user
                );
            }
        }
        settingCurrentUser()
    }, [user]);

    useEffect(() => {
        if (currentUser) {
            socket.current = io("http://localhost:3001");
            socket.current.emit("new-user", currentUser._id);
        }
    }, [currentUser]);

    useEffect(() => {
        const fetchAllUser = async () => {
            if (currentUser) {
                fetch(`http://localhost:3001/api/user/allusers/${currentUser._id}`)
                    .then((res) => res.json())
                    .then((data) => {
                        setContacts(data)

                    });
            }
        }
        fetchAllUser()
    }, [currentUser]);

    const handleChatChange = (chat) => {
        setCurrentChat(chat);
    };
    return (
        <>
            <div className='contain'>
                <Contacts contacts={contacts} changeChat={handleChatChange} />
                {currentChat === undefined ?
                    (<h1>No chat selected</h1>) :
                    (<ChatContainercopy socket={socket} userId={user._id} currentChat={currentChat} />)

                }
            </div>
        </>
    );
}
