import React, { useState, useEffect, useRef } from "react";
import { io } from "socket.io-client";
import { useAuthContext } from '../hooks/useAuthContext'
import axios from "axios";
// CSS
import './Chat.css'

//Component
import ChatContainercopy from '../components/ChatContainercopy'
import Contacts from '../components/Contacts';

export default function Chat() {
    // const socket = io.connect("http://localhost:3001");
    const socket = useRef();
    const [currentRoom, setCurrentRoom] = useState([]);


    const [contacts, setContacts] = useState([]);
    const { user } = useAuthContext()
    const [currentUser, setCurrentUser] = useState({});
    const [currentChat, setCurrentChat] = useState(undefined);
    // const [room, setCurrentRoom] = useState("general");
    console.log(`http://localhost:3001/api/user/allusers/${user.id}`)

    useEffect(() => {
        const getCurrentUser = () => {
            if (user) {
                setCurrentUser(user);
            }
        }
        getCurrentUser()
    }, []);
    console.log("current " + currentUser)

    useEffect(() => {
        if (user) {
            socket.current = io("http://localhost:3001");
            socket.current.emit("new-user", user.id);
        }
    }, [user]);

    useEffect(() => {
        const fetchAllUser = async () => {
            if (user) {
                const data = await axios.get(`http://localhost:3001/api/user/allusers/${user.id}`);
                setContacts(data.data);
                console.log(data.data)
            }
        }
        fetchAllUser()

    }, [user]);
    console.log(contacts)

    const handleChatChange = (chat) => {
        setCurrentChat(chat);
    };

    // function getRooms() {
    //     fetch("http://localhost:3001/api/rooms")
    //         .then((res) => res.json())
    //         .then((data) => {
    //             setRooms(data)
    //         });

    // }
    // console.log(rooms)
    // console.log("room:" + rooms)

    // const joinRoom = (room) => {
    //     if (user.username !== "" && room !== "") {
    //         socket.emit("join_room", room, currentRoom);
    //         setCurrentRoom(room)
    //     }
    // };


    // const joinRoom = () => {
    //     setRoom("general")
    //     if (user.username !== "" && room !== "") {
    //         socket.emit("join_room", room);
    //     }
    // };

    return (
        <>
            <div className='contain'>
                <Contacts contacts={contacts} changeChat={handleChatChange} />
                {/* <Contacts /> */}
                {/* <ChatContainercopy setMessage={messages} /> */}
                <ChatContainercopy socket={socket} username={user.username} room={currentRoom} />

            </div>

        </>
    );
}
