import React, { useEffect, useState } from "react";
import './Contacts.css'
import { Col, ListGroup, Row } from "react-bootstrap";
import { useAuthContext } from '../hooks/useAuthContext'



export default function Contacts({ socket, onJoinRoom }) {
    const [currentRoom, setCurrentRoom] = useState([]);
    const [rooms, setRooms] = useState([]);
    const [privateMemberMsg, setPrivateMemberMsg] = useState({});
    const [members, setMembers] = useState([]);
    const { user } = useAuthContext()



    useEffect(() => {
        if (user) {
            setCurrentRoom("general");
            getRooms();
            socket.emit("join-room", "general");
            socket.emit("new-user");
        }
    }, []);
    socket.off("new-user").on("new-user", (members) => {
        setMembers(members);
    });
    // console.log("members", members)


    // function getRooms() {
    //     fetch("http://localhost:5001/rooms")
    //         .then((res) => res.json())
    //         .then((data) => setRooms(data));
    // }

    // function orderIds(id1, id2) {
    //     if (id1 > id2) {
    //         return id1 + "-" + id2;
    //     } else {
    //         return id2 + "-" + id1;
    //     }
    // }

    function handlePrivateMemberMsg(member) {
        setPrivateMemberMsg(member);
        const roomId = orderIds(user._id, member._id);
        onJoinRoom(roomId, false);
    }

    if (!user) {
        return <></>;
    }
    return (
        < div className='users-card' >
            {members.map((member) => (
                <div key={member.id} active={privateMemberMsg?._id == member?._id} className="user-details" onClick={() => handlePrivateMemberMsg(member)} disabled={member._id === user._id}>

                    <h3 className="username">{member.username}</h3>

                </div>
            ))}
        </div >
    )

}
