import React, { useState, useEffect, useRef } from "react";
import { io } from "socket.io-client";
import { useAuthContext } from '../hooks/useAuthContext'

// CSS
import './Chat.css'

//Component
import ChatContainercopy from '../components/ChatContainercopy'
import Contacts from '../components/Contacts';

export default function Chat() {
    const socket = io.connect("http://localhost:3001");
    const { user } = useAuthContext()
    const [currentUser, setCurrentUser] = useState({})
    const [rooms, setRooms] = useState([]);
    const [currentRoom, setCurrentRoom] = useState([]);
    // const [room, setCurrentRoom] = useState("general");
    console.log(user._userID)

    // useEffect(() => {
    //     if (user) {
    //         setCurrentUser(user)
    //         socket.emit("new-user", currentUser._id);
    //     }
    //     console.log("c" + currentUser._id);
    // }, [currentUser]);


    function getRooms() {
        fetch("http://localhost:3001/api/rooms")
            .then((res) => res.json())
            .then((data) => {
                setRooms(data)
            });

    }
    console.log(rooms)
    console.log("room:" + rooms)

    const joinRoom = (room) => {
        if (user.username !== "" && room !== "") {
            socket.emit("join_room", room, currentRoom);
            setCurrentRoom(room)
        }
    };


    // const joinRoom = () => {
    //     setRoom("general")
    //     if (user.username !== "" && room !== "") {
    //         socket.emit("join_room", room);
    //     }
    // };

    return (
        <>
            <div className='contain'>
                <Contacts socket={socket} onJoinRoom={joinRoom} />
                {/* <Contacts /> */}
                {/* <ChatContainercopy setMessage={messages} /> */}
                <ChatContainercopy socket={socket} username={user.username} room={currentRoom} />

            </div>

        </>
    );
}
