import React, { useState, useEffect } from "react";
// import React from "react";
import './Contacts.css'
import { useAuthContext } from '../hooks/useAuthContext'
import { io } from "socket.io-client";





export default function Contacts({ socket, contacts, changeChat }) {
    // const [currentRoom, setCurrentRoom] = useState([]);
    // const [rooms, setRooms] = useState([]);

    const [currentUserName, setCurrentUserName] = useState(undefined);
    const [currentSelected, setCurrentSelected] = useState(undefined);
    const [currentRoom, setCurrentRoom] = useState(undefined);
    const { user } = useAuthContext()

    const customStyles = {
        control: (base, state) => ({
            ...base,
            background: "#023950",
            // match with the menu
            borderRadius: state.isFocused ? "3px 3px 0 0" : 3,
            // Overwrittes the different states of border
            borderColor: state.isFocused ? "yellow" : "green",
            // Removes weird border around container
            boxShadow: state.isFocused ? null : null,
            "&:hover": {
                // Overwrittes the different states of border
                borderColor: state.isFocused ? "red" : "blue"
            }
        })
    }
    useEffect(() => {
        if (user) {
            setCurrentUserName(user.username);
        }
    }, []);


    function joinRoom(room, isPublic = false) {
        if (!user) {
            return alert("Please login");
        }
        socket.current.emit("join-room", room, currentRoom);
        setCurrentRoom(room);

    };

    function orderIds(id1, id2) {
        if (id1 > id2) {
            return id1 + "-" + id2;
        } else {
            return id2 + "-" + id1;
        }
    }

    const changeCurrentChat = (index, contact) => {
        setCurrentSelected(index);
        changeChat(contact)
        // const roomId = orderIds(user._id, contact._id);
        // joinRoom(roomId, false)
        // changeChat(currentRoom);
        // setPrivateMemberMsg(contact);
        joinRoom(contact._id);

    }





    // function handlePrivateMemberMsg(member) {
    //     setPrivateMemberMsg(member);
    //     const roomId = orderIds(user._id, member._id);
    //     onJoinRoom(roomId, false);
    // }
    return (
        <>
            {(user &&
                < div className='users-card' >
                    {contacts.filter((contact) => contact.status == "online").map((contact, index) => {
                        return (
                            <div key={contact._id} onClick={() => changeCurrentChat(index, contact)} style={`user-details ${index === currentSelected ? `users-card user-details selected` : ''
                                }`}  >
                                <h3 className="username">{contact.username}</h3>
                            </div>
                        );
                    }
                    )}
                </div >
            )
            }
        </>
    )

}
