import React, { useState } from "react";
// import React from "react";
import './Contacts.css'
import { useAuthContext } from '../hooks/useAuthContext'
const socket = io.connect("http://localhost:3001");




export default function Contacts({ contacts, changeChat }) {
    // const [currentRoom, setCurrentRoom] = useState([]);
    // const [rooms, setRooms] = useState([]);
    // const [privateMemberMsg, setPrivateMemberMsg] = useState({});
    const [currentSelected, setCurrentSelected] = useState(undefined);
    const { user } = useAuthContext()

    function joinRoom(room) {
        if (!user) {
            return alert("Please login");
        }
        socket.emit("join-room", room);
        setCurrentRoom(room);

    }

    socket.off("notifications").on("notifications", (room) => {
        if (currentRoom != room) dispatch(addNotifications(room));
    });

    const changeCurrentChat = (index, contact) => {
        setCurrentSelected(index);
        changeChat(contact);
    }


    console.log(currentSelected)



    // function handlePrivateMemberMsg(member) {
    //     setPrivateMemberMsg(member);
    //     const roomId = orderIds(user._id, member._id);
    //     onJoinRoom(roomId, false);
    // }
    return (
        <>
            {(user &&
                < div className='users-card' >
                    {contacts.filter((contact) => contact.status == "online").map((contact, index) => {
                        return (
                            <div key={contact._id} onClick={() => changeCurrentChat(index, contact)} className={`user-details ${index === currentSelected ? "selected" : "selected"
                                }`}  >
                                <h3 className="username">{contact.username}</h3>
                            </div>
                        );
                    }
                    )}
                </div >
            )
            }
        </>
    )

}
