import React, { useEffect, useRef, useState } from "react";
import { io } from "socket.io-client";
import './Chat.css'
import ChatContainercopy from '../components/ChatContainercopy'
import Contacts from '../components/Contacts';
// import { useAuthContext } from '../hooks/useAuthContext'

import { host } from "../utils/APIRoutes";


const API_URL = process.env.REACT_APP_API_URL;

export default function Chat() {
    // const { user } = useAuthContext()
    const socket = useRef();
    const [currentChat, setCurrentChat] = useState(undefined);
    const [currentUser, setCurrentUser] = useState(undefined);

    useEffect(() => {
        async function fecthCurrentUser() {
            if (localStorage.getItem(API_URL)) {
                setCurrentUser(
                    await JSON.parse(
                        localStorage.getItem(API_URL)
                    )
                );
            }
        }
        fecthCurrentUser()
    }, []);

    useEffect(() => {
        if (currentUser) {
            socket.current = io(host);
            socket.current.emit("add-user", currentUser._id);
        }
    }, [currentUser]);
    //handle change of conversation
    const handleChatChange = (chat) => {
        setCurrentChat(chat);
    };

    return (
        <>

            <div className='contain'>
                <Contacts changeChat={handleChatChange} />
                {/* <Contacts /> */}
                <ChatContainercopy currentChat={currentChat} socket={socket} />
                {/* <ChatContainercopy  /> */}

            </div>

        </>
    );
}
