import React, { useEffect, useState, useRef } from "react";
import './ChatContainercopy.css'
import { useAuthContext } from '../hooks/useAuthContext'


// import Chatinput from "./Chatinput";
export default function ChatContainercopy({ socket, userId, currentChat }) {
    const [currentMessage, setCurrentMessage] = useState("");
    const [messageList, setMessageList] = useState([]);
    const messageEndRef = useRef(null);
    const { user } = useAuthContext()

    //scroll smothly
    useEffect(() => {
        scrollToBottom();
    }, [messageList]);

    function scrollToBottom() {
        messageEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }

    //Get current chat


    useEffect(() => {
        const getCurrentChat = async () => {
            if (currentChat) {
                await JSON.parse(
                    localStorage.getItem(process.env.REACT_APP_LOCALHOST_KEY)
                )._id;
            }
        };
        getCurrentChat();
    }, []);

    function getFormattedDate() {
        const date = new Date();
        const year = date.getFullYear();
        let month = (1 + date.getMonth()).toString();
        month = month.length > 1 ? month : "0" + month;
        let day = date.getDate().toString();
        day = day.length > 1 ? day : "0" + day;
        return month + "/" + day + "/" + year;
    }
    const todayDate = getFormattedDate();

    function joinRoom(room, isPublic = true) {
        if (!user) {
            return alert("Please login");
        }
        socket.current.emit("join-room", room);
        // setCurrentRoom(room);
    };
    useEffect(() => {
        joinRoom(currentChat._id, false);
    }, [currentChat._id])

    //Get the room messages
    useEffect(() => {
        socket.current.on("room-messages", (roomMessages) => {
            setMessageList(roomMessages);
        });
    }, []);

    // send message to room(user)
    const sendMessage = async () => {
        if (currentMessage !== "") {
            const today = new Date();
            const room = currentChat._id
            const author = user.username
            const minutes = today.getMinutes() < 10 ? "0" + today.getMinutes() : today.getMinutes();
            const time = today.getHours() + ":" + minutes;
            const content = currentMessage
            await socket.current.emit("message-room", room, content, author, time, todayDate);
            // setMessageList((list) => [...list, messageData]);
            setCurrentMessage("");
        }
    }
    return (
        <>
            {currentChat && (
                <div className='chat-container'>
                    <div className="currentChat"><p>{currentChat.username}</p></div>
                    <div className="message-container">
                        {/* 2 */}
                        {user &&
                            messageList.map(({ _id: date, messagesByDate }, idx) => (
                                <div key={idx}>
                                    {messagesByDate.map(({ content, time, from: sender }, msgIdx) => (
                                        <div className="message" key={msgIdx}>

                                            <div>
                                                <div className="message-content">
                                                    <p>{content}</p>
                                                </div>
                                                <div className="message-meta">
                                                    <p id="time">{time}</p>
                                                    <p id="author">{sender}</p>
                                                </div>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            ))}

                        {/* 3 */}
                        {/* {messageList.map((message) => {
                            return (
                                <div ref={scrollRef} key={uuidv4()}>
                                    <div
                                        className={`message ${message.fromSelf ? "sended" : "recieved"
                                            }`}
                                    >
                                        <div className="message-content">
                                            <p>{message.message}</p>
                                        </div>
                                    </div>
                                </div>
                            );
                        })} */}
                        <div ref={messageEndRef} />
                    </div>
                    <div className="chat-input" >
                        <input onChange={(e) => setCurrentMessage(e.target.value)} onKeyPress={(event) => {
                            event.key === "Enter" && sendMessage();
                        }}
                            value={currentMessage} type="text" className="txt-input" placeholder="Send a message..." />
                        <button onClick={sendMessage} type="submit" className="btn-submit" id="chat-submit" disabled={!user}>send</button>
                    </div>
                    {/* <Chatinput /> */}
                </div>
            )}
        </>
    )
}
